import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import TextField from '@material-ui/core/TextField'
import Button from '@material-ui/core/Button';

function App() {
  const [text, setText] = useState('')
  const [gifs, setGifs] = useState([])

  async function getGifs(){
    console.log("Get information")
    const key = 'voimtxRjvTMO2LtI4kAD6xBDySCuplT1'
    let url = "https://api.giphy.com/v1/gifs/search?"
    url += 'api_key=' + key
    url += '&q=' + text
    /** Using await methods (or asynchronous methods) requires async methods */
    const r = await fetch(url)
    const body = await r.json()
    setGifs(body.data)
    setText('')
  }

  console.log(gifs)

  return (
    <div className="App">
      <header className="App-header">
        <div className="input-wrap">
          {/** This is a text field component from material-ui */}
          <TextField id="outlined-basic" 
            label="Enter text" 
            fullWidth variant="outlined"
            value={text}
            onChange={e=> setText(e.target.value)}
            onKeyPress={e=>{
              if(e.key==='Enter') getGifs()
          }}/>
          {/** this is a button component from material-ui */}
          <Button variant="contained" color="primary"
            onClick={getGifs}>
            send
          </Button>
        </div>
        <div className="gifs">
          {gifs.map((gif,i)=> <Gif key={i} {...gif}/>)}
        </div>
      </header>
    </div>
  );
}

/**Gif component
 * You'll probably want to make this in another file next time
 */
function Gif({title, images}){
  return <div className="gif">
    <img src={images.fixed_height.url} alt="gif"/>
    <div className="gif-title">{title}</div>
  </div>
}

export default App;
